<template>
  <div class="contentImg">
    <div v-if="!readLoadInit">
      <home v-if="login" />
      <lista v-else />
    </div>
    <section v-else>
      <div class="container">
        <load />
      </div>
    </section>
  </div>
</template>

<script>
  import home from "@/components/avaliacao/home";
  import lista from "@/components/avaliacao/lista";
  import load from "@/components/avaliacao/elements/progressLoading.vue";

  import { useAvaliacaoStore } from '@/store/AvaliacaoStore'
  const avaliacaoStore = useAvaliacaoStore() 

  import { useGeralStore } from '@/store/GeralStore'
  const geralStore = useGeralStore()

  export default {
    components:{
      home, lista, load
    },
    computed:{
      login(){
        const ava = avaliacaoStore.readLogin.id
        if(ava){
          return false
        }
        return true
      },
      readLoadInit(){
        return avaliacaoStore.readLoadInit
      },
      readLoadUpdatePage(){
        return avaliacaoStore.readLoadUpdatePage
      }
    },
    created(){
      geralStore.changeTitleApp({title:'Avaliação', to:'/avaliacao'})
      avaliacaoStore.loadUserData()
      avaliacaoStore.cargaInicial()
    }
  }

</script>

